import './App.css';
import axios from 'axios';
import { useState,useEffect } from 'react';

const api = axios.create({
  baseURL:`http://localhost:3000/users`
})



function App() {

  const [users,setusers] = useState([])

  async function createUser(){
    let res = await api.post('/',{id:6,first_name:'sagar',last_name:'chhatbar',email:'sagar@gmail.com'}).catch(err=>console.log(err))
    console.log(res);
  }

  async function getUsers(){
   let data = await api.get('/',{params:{
     _limit:6,
   }}).then(({data}) => data)
   setusers(data);
  }

  const deleteUser = async (id) => {
    let data = await api.delete(`/${id}`)
    getUsers();
  }

  const updateUser = async (id,val) => {
    let data = await api.patch(`/${id}`,{first_name:val})
    getUsers();
  }


  useEffect(()=>{
    getUsers();
  },[users])

  return (
    <div className="App">
        <button onClick={createUser}>create user</button>
        {users.map(user => <h2 key={user.id} onClick={()=>updateUser(user.id,`${user.first_name}Clicked`)}>{user.first_name}<button onClick={()=>deleteUser(user.id)}>X</button></h2>)}
    </div>
  );
}

export default App;
