import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
import Form from './Component/Form';




const api = axios.create({
  baseURL:`http://localhost:3000/users`
})



function App() {
  const [users, setusers] = useState([]);
  const getUsers = () => { api.get('/').then((res) => setusers(res.data)).catch(err => { console.log(err) }) }

  useEffect(() => {
    getUsers()
  }, [])


  async function createUser() {
    await axios.post('/', { id: 6, first_name: 'sagar', last_name: 'chhatbar', email: 'sagar@gmail.com' }).catch(err => console.log(err))
    getUsers()
  }

  const deleteUser = async (id) => {
    await axios.delete(`/${id}`)
    getUsers()

  }

  const updateUser = async (id, val) => {
    await axios.patch(`/${id}`, { first_name: val })
    getUsers()
  }

  return (
    <div className="App">
      {users?.map(user =>
        <div key={user.id} style={{ display: 'flex', justifyContent: 'center' }}>
          <h2 onClick={() => updateUser(user.id, `${user.first_name}Clicked`)}>
            {user.first_name}
          </h2>
          <button onClick={() => deleteUser(user.id)}>X</button>
        </div>
      )}
      <button onClick={createUser}>create user</button>

    </div>
  );
}

export default App;
