import './App.css';
import axios from 'axios';
import { useState, useEffect, useLayoutEffect } from 'react';
import Form from './Component/Form';





const baseURL = `http://localhost:3000/users`
function App() {
  const [users, setusers] = useState([]);
  const getUsers = () => { axios.get(baseURL + '/').then((res) => setusers(res.data)).catch(err => { console.log(err) }) }
  useEffect(() => {
    getUsers()
    console.log("here")
  }, [])


  async function createUser() {
    let res = await axios.post(baseURL + '/', { id: 6, first_name: 'sagar', last_name: 'chhatbar', email: 'sagar@gmail.com' }).catch(err => console.log(err))
    getUsers()
  }

  const deleteUser = async (id) => {
    await axios.delete(baseURL + `/${id}`)
    getUsers()

  }

  const updateUser = async (id, val) => {
    await axios.patch(`/${id}`, { first_name: val })
    getUsers()
  }

  // useLayoutEffect(() => {
  //   getUsers();
  // }, [getUsers])




  return (
    <div className="App">
      {users?.map(user =>
        <div>
          <h2 key={user.id} onClick={() => updateUser(user.id, `${user.first_name}Clicked`)}>
            {user.first_name}
          <button onClick={() => deleteUser(user.id)}>X</button>
          </h2>
        </div>
      )}
      <button onClick={createUser}>create user</button>

    </div>
  );
}

export default App;
