import './App.css';
import axios from 'axios';
import { useState, useEffect, useLayoutEffect } from 'react';
import Form from './Component/Form';





function App() {

  const [users, setusers] = useState([]);



  useEffect(() => {

    const api = axios.create({
      baseURL: `http://localhost:3000/users`
    })

    async function getUsers() {
      let data = await api.get('/').then(({ data }) => data).catch(err => { console.log(err) })
      setusers(data);
      console.log(data)
    }
    getUsers();
  }, [])


  async function createUser() {
    let res = await api.post('/', { id: 6, first_name: 'sagar', last_name: 'chhatbar', email: 'sagar@gmail.com' }).catch(err => console.log(err))
    console.log(res);
  }

  const deleteUser = async (id) => {
    let data = await api.delete(`/${id}`)
  }

  const updateUser = async (id, val) => {
    let data = await api.patch(`/${id}`, { first_name: val })

  }

  // useLayoutEffect(() => {
  //   getUsers();
  // }, [getUsers])




  return (
    <div className="App">
      {users.map(user => <h2 key={user.id} onClick={() => updateUser(user.id, `${user.first_name}Clicked`)}>
        {user.first_name}
        <button onClick={() => deleteUser(user.id)}>X</button></h2>)}
      <button onClick={createUser}>create user</button>
    </div>
  );
}

export default App;
