import axios from 'axios';
import { useState, useEffect, useLayoutEffect } from 'react';
import Form from './Component/Form';





const baseURL = `http://localhost:3000/users`
function App() {
  const [users, setusers] = useState([]);
  useEffect(() => {
      console.log("here")
      const data = axios.get(baseURL + '/').then(({ data }) => data).catch(err => { console.log(err) })
      setusers(data);
      console.log(users,data)
  }, [])


  async function createUser() {
    let res = await axios.post(baseURL + '/', { id: 6, first_name: 'sagar', last_name: 'chhatbar', email: 'sagar@gmail.com' }).catch(err => console.log(err))
    console.log(res);
  }

  // const deleteUser = async (id) => {
  //   let data = await api.delete(`/${id}`)
  // }

  const updateUser = async (id, val) => {
    let data = await axios.patch(`/${id}`, { first_name: val })

  }

  // useLayoutEffect(() => {
  //   getUsers();
  // }, [getUsers])




  return (
    <div className="App">
      {users?.map(user => <h2 key={user.id} onClick={() => updateUser(user.id, `${user.first_name}Clicked`)}>
        {user.first_name}
        {/* <button onClick={() => deleteUser(user.id)}>X</button> */}
        </h2>)}
      <button onClick={createUser}>create user</button>
    </div>
  );
}

export default App;
